package Logical_practise;
import java.util.*;
public class Minutes_to_sec {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc= new Scanner(System.in);
		System.out.println("Enter the number to conver into seconds:");
		int n=sc.nextInt();
		System.out.println(n+"="+n*60+"Seconds");

	}

}
